#Create the roof
obstacles = []
for i in range(width):
    h = random.randint(8,12)
    obstacles.append(h)
    RUR.add_wall("south",i+1,h)

#join the roof (vertically on the sides)
for i in range(width-1):
    n = obstacles[i]
    n2 = obstacles[i+1]
    if n < n2:
        for j in range(n,n2):
            RUR.add_wall("east",i+1,j)    
    elif n2 < n:
        for j in range(n2,n):
            RUR.add_wall("east",i+1,j)  


#create goals
